import React, {Component} from 'react';
import {connect} from 'react-redux'
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
// import {getBestSeller, getMensWear, getWomensWear} from '../../../services/index'
// import {addToCart, addToWishlist, addToCompare} from "../../../actions/index";
import ProductItem from '../../../../components/layouts/common/product-item';

import Commerce from "@chec/commerce.js";
const commerce = new Commerce(
    "pk_25095f6ed4dac89f69f1e4337484555ec6c07ef2fc5d8"
  );



class Details extends Component {
    
    constructor(props) {
        super(props); 

        this.state = { 
        products: [],
        };
    }
    componentDidMount() {
        commerce.products.list().then((result) => {
          this.setState({ products: result.data });
        });
      }

    render (){
        const {item, addToWishlistClicked, addToWishlist,ClickedbestSeller,mensWear,womensWear, symbol, addToCart, addToCompare} = this.props

        return (
            <div>
                <div className="title1 section-t-space">
                    <h4>exclusive products</h4>
                    <h2 className="title-inner1">special products</h2>
                </div>
                <section className="section-b-space p-t-0">
                    <div className="container">
                        <Tabs className="theme-tab">
                            <TabList className="tabs tab-title">
                                <Tab>New Products</Tab>
                                <Tab>Mens Wear</Tab>
                                <Tab>Womens Wear</Tab>
                            </TabList>

                            <TabPanel>
                                <div className="no-slider row">
                                    {/* { bestSeller.map((product, index ) => */}
                                    { this.state.products.map((product, index ) =>
                                        <ProductItem product={product} symbol={symbol}
                                                     onAddToCompareClicked={() => addToCompare(product)}
                                                     onAddToWishlistClicked={() => addToWishlist(product)}
                                                     onAddToCartClicked={() => addToCart(product, 1)} key={index} /> )
                                    }
                                </div>
                            </TabPanel>
                            <TabPanel>
                                <div className="no-slider row">
                                    { this.state.products.map((product, index ) =>
                                        <ProductItem product={product} symbol={symbol}
                                                     onAddToCompareClicked={() => addToCompare(product)}
                                                     onAddToWishlistClicked={() => addToWishlist(product)}
                                                     onAddToCartClicked={() => addToCart(product, 1)} key={index} /> )
                                    }
                                </div>
                            </TabPanel>
                            <TabPanel>
                                <div className=" no-slider row">
                                    { this.state.products.map((product, index ) =>
                                        <ProductItem product={product} symbol={symbol}
                                                     onAddToCompareClicked={() => addToCompare(product)}
                                                     onAddToWishlistClicked={() => addToWishlist(product)}
                                                     onAddToCartClicked={() => addToCart(product, 1)} key={index} /> )
                                    }
                                </div>
                            </TabPanel>
                        </Tabs>
                    </div>
                </section>
            </div>
            // <div className="col-lg-4">
            //     <div className="product-right product-description-box">
            //         <h2> {item.name} </h2>
            //         <div className="border-product">
            //             <h6 className="product-title">product details</h6>
            //             <p>{item.shortDetails}</p>
            //         </div>
            //         <div className="single-product-tables border-product detail-section">
            //             <table>
            //                 <tbody><tr>
            //                     <td>Febric:</td>
            //                     <td>Chiffon</td>
            //                 </tr>
            //                 <tr>
            //                     <td>Color:</td>
            //                     <td>{item.variants[0].color}</td>
            //                 </tr>
            //                 <tr>
            //                     <td>Avalibility:</td>
            //                     <td>InStock</td>
            //                 </tr>
            //                 </tbody>
            //             </table>
            //         </div>
            //         <div className="border-product">
            //             <h6 className="product-title">share it</h6>
            //             <div className="product-icon">
            //                 <ul className="product-social">
            //                     <li><a href="https://www.facebook.com/" target="_blank"><i className="fa fa-facebook"></i></a></li>
            //                     <li><a href="https://plus.google.com/discover" target="_blank"><i className="fa fa-google-plus"></i></a></li>
            //                     <li><a href="https://twitter.com/" target="_blank"><i className="fa fa-twitter"></i></a></li>
            //                     <li><a href="https://www.instagram.com/" target="_blank"><i className="fa fa-instagram"></i></a></li>
            //                 </ul>
            //                     <button className="wishlist-btn" onClick={() => addToWishlistClicked(item)}>
            //                         <i className="fa fa-heart"></i><span className="title-font">Add To WishList</span>
            //                     </button>
            //             </div>
            //         </div>
            //         <div className="border-product">
            //             <h6 className="product-title">100% SECURE PAYMENT</h6>
            //             <div className="payment-card-bottom">
            //                 <ul>
            //                     <li>
            //                         <a href="#"><img src={`${process.env.PUBLIC_URL}/assets/images/icon/visa.png`} alt="" /></a>
            //                     </li>
            //                     <li>
            //                         <a href="#"><img src={`${process.env.PUBLIC_URL}/assets/images/icon/mastercard.png`} alt="" /></a>
            //                     </li>
            //                     <li>
            //                         <a href="#"><img src={`${process.env.PUBLIC_URL}/assets/images/icon/paypal.png`} alt="" /></a>
