            //                     </li>
            //                     <li>
            //                         <a href="#"><img src={`${process.env.PUBLIC_URL}/assets/images/icon/discover.png`} alt="" /></a>
            //                     </li>
            //                 </ul>
            //             </div>
            //         </div>
            //     </div>
            // </div>
        )
    }
}


export default Details;
