import React, {Component} from 'react';
import {Link} from 'react-router-dom'
import product from '../../../layouts/common/products';

const CartHeader  = ({item, product, total, symbol, removeFromCart}) => (
    <li >
        <div className="media">
            <h2>{product.media.source}</h2>
            <Link to={`${process.env.PUBLIC_URL}/product/${product.id}`}><img alt="" className="mr-3" src={`${product.media.source}`} /></Link>
            <div className="media-body">
                <Link to={`${process.env.PUBLIC_URL}/product/${product.id}`}><h4>{product.name}</h4></Link>
                <h4><span>{product.qty} x {symbol} {product.price.formatted}</span></h4>
            </div>
        </div>
        {/*<span>{cart}</span>*/}
        <div className="close-circle">
            <a href={null} onClick={ removeFromCart}><i className="fa fa-times" aria-hidden="true"></i></a>
        </div>
    </li>
)




export default CartHeader;
